import React from 'react';
import { cn } from '../../utils/cn';

const AuthCard = ({ children, className, ...props }) => {
  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-gradient-to-br from-primary-50 via-white to-primary-100">
      <div
        className={cn(
          "w-full max-w-md glass-card rounded-2xl p-8 shadow-soft",
          "animate-fade-in transform transition-all duration-500",
          "hover:shadow-glass hover:scale-[1.02]",
          "border border-white/30",
          className
        )}
        {...props}
      >
        {children}
      </div>
    </div>
  );
};

export default AuthCard;
