import React from 'react';
import { cn } from '../../utils/cn';

const AuthCard = ({ children, className, ...props }) => {
  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-[#EDE9FE]">
      <div
        className={cn(
          "w-full max-w-md bg-white/80 backdrop-blur-sm rounded-xl p-8",
          "border border-[#EDE9FE] shadow-sm",
          "animate-fade-in transform transition-all duration-300",
          "hover:shadow-md hover:scale-[1.01]",
          className
        )}
        {...props}
      >
        {children}
      </div>
    </div>
  );
};

export default AuthCard;
