import React from 'react'
import { LayoutDashboard, Calendar, Users, CreditCard, Settings } from 'lucide-react'

const items = [
  { key: 'overview', label: 'Dashboard', icon: LayoutDashboard },
  { key: 'sessions', label: 'Manage Sessions', icon: Calendar },
  { key: 'users', label: 'Registered Users', icon: Users },
  { key: 'payments', label: 'Payments', icon: CreditCard },
  { key: 'settings', label: 'Settings', icon: Settings }
]

const Sidebar = ({ active, onChange }) => {
  return (
    <aside className="fixed inset-y-0 left-0 w-64 bg-gradient-to-b from-purple-700 to-purple-900 text-white px-3 py-5">
      <div className="px-2 mb-4">
        <div className="text-lg font-semibold">Admin Panel</div>
      </div>
      <nav className="space-y-1">
        {items.map(({ key, label, icon: Icon }) => (
          <button
            key={key}
            onClick={() => onChange(key)}
            className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg transition-colors ${
              active === key ? 'bg-white/15' : 'hover:bg-white/10'
            }`}
          >
            <Icon className="h-5 w-5" />
            <span className="text-sm font-medium">{label}</span>
          </button>
        ))}
      </nav>
    </aside>
  )
}

export default Sidebar
