import React, { useState, useRef, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Link } from 'react-router-dom'
import { 
  Mail, 
  ArrowRight, 
  ArrowLeft, 
  CheckCircle, 
  User, 
  Eye, 
  EyeOff, 
  Lock,
  Key
} from 'lucide-react'
import { Button } from '../../components/ui/Button'
import { Input } from '../../components/ui/Input'
import { Label } from '../../components/ui/Label'
import AuthCard from '../../components/common/AuthCard'

const RegisterPage = () => {
  const [currentStep, setCurrentStep] = useState(1) // 1: Email, 2: OTP & Registration
  const [isLoading, setIsLoading] = useState(false)
  const [errors, setErrors] = useState({})
  const [otpSent, setOtpSent] = useState(false)
  const [resendCooldown, setResendCooldown] = useState(0)
  const [showPassword, setShowPassword] = useState(false)
  const [successMessage, setSuccessMessage] = useState('')
  
  // Form data
  const [formData, setFormData] = useState({
    email: '',
    otp: ['', '', '', '', '', ''],
    firstName: '',
    lastName: '',
    password: '',
    confirmPassword: ''
  })

  // OTP input refs
  const otpInputs = useRef([])

  // Countdown effect for resend
  useEffect(() => {
    if (resendCooldown > 0) {
      const timer = setTimeout(() => setResendCooldown(resendCooldown - 1), 1000)
      return () => clearTimeout(timer)
    }
  }, [resendCooldown])

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }))
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }))
    }
  }

  const handleOtpChange = (index, value) => {
    if (value.length > 1) return // Prevent multiple characters
    
    const newOtp = [...formData.otp]
    newOtp[index] = value
    setFormData(prev => ({ ...prev, otp: newOtp }))
    
    // Auto-focus next input
    if (value && index < 5) {
      otpInputs.current[index + 1]?.focus()
    }
  }

  const handleOtpKeyDown = (e, index) => {
    if (e.key === 'Backspace' && !formData.otp[index] && index > 0) {
      otpInputs.current[index - 1]?.focus()
    }
  }

  const validateEmail = () => {
    const newErrors = {}
    if (!formData.email) {
      newErrors.email = 'Email is required'
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email'
    }
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const validateOtp = () => {
    const otpString = formData.otp.join('')
    const newErrors = {}
    if (otpString.length !== 6) {
      newErrors.otp = 'Please enter the complete 6-digit code'
    }
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const validateRegistration = () => {
    const newErrors = {}
    
    if (!formData.firstName.trim()) {
      newErrors.firstName = 'First name is required'
    }
    
    if (!formData.lastName.trim()) {
      newErrors.lastName = 'Last name is required'
    }
    
    if (!formData.password) {
      newErrors.password = 'Password is required'
    } else if (formData.password.length < 8) {
      newErrors.password = 'Password must be at least 8 characters'
    }
    
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match'
    }
    
    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleEmailSubmit = async (e) => {
    e.preventDefault()
    if (!validateEmail()) return
    
    setIsLoading(true)
    try {
      await new Promise(resolve => setTimeout(resolve, 1500)) // Simulate API call
      setOtpSent(true)
      setCurrentStep(2)
      setResendCooldown(60)
    } catch (error) {
      console.error('Error sending OTP:', error)
    } finally {
      setIsLoading(false)
    }
  }

  const handleOtpSubmit = async (e) => {
    e.preventDefault()
    if (!validateOtp()) return
    
    setIsLoading(true)
    try {
      await new Promise(resolve => setTimeout(resolve, 1500)) // Simulate API call
      setCurrentStep(3)
    } catch (error) {
      console.error('Error verifying OTP:', error)
      setErrors({ otp: 'Invalid verification code. Please try again.' })
    } finally {
      setIsLoading(false)
    }
  }

  const handleRegistrationSubmit = async (e) => {
    e.preventDefault()
    if (!validateRegistration()) return
    
    setIsLoading(true)
    try {
      await new Promise(resolve => setTimeout(resolve, 2000)) // Simulate API call
      console.log('Registration successful:', formData)
      // Handle successful registration
    } catch (error) {
      console.error('Registration error:', error)
    } finally {
      setIsLoading(false)
    }
  }

  const handleResendOtp = async () => {
    if (resendCooldown > 0) return
    
    setIsLoading(true)
    try {
      await new Promise(resolve => setTimeout(resolve, 1000))
      setResendCooldown(60)
    } catch (error) {
      console.error('Error resending OTP:', error)
    } finally {
      setIsLoading(false)
    }
  }

  const getStepTitle = () => {
    switch (currentStep) {
      case 1: return "Join DivineSpark"
      case 2: return "Verify & Complete"
      default: return ""
    }
  }

  const getStepDescription = () => {
    switch (currentStep) {
      case 1: return "Enter your email address to begin your DivineSpark journey"
      case 2: return `We've sent a 6-digit code to ${formData.email}. Please enter it below.`
      default: return ""
    }
  }

  return (
    <AuthCard>
      {/* Back Button */}
      <div className="mb-6">
        {currentStep > 1 ? (
          <Button 
            variant="ghost" 
            onClick={() => setCurrentStep(currentStep - 1)}
            className="text-gray-500 hover:text-[#6D28D9]"
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back
          </Button>
        ) : (
          <Link to="/">
            <Button variant="ghost" className="text-gray-500 hover:text-[#6D28D9]">
              <ArrowLeft className="mr-2 h-4 w-4" />
              Back to Home
            </Button>
          </Link>
        )}
      </div>

      {/* Progress Indicator */}
      <div className="mb-8">
        <div className="flex items-center justify-center space-x-4">
          {[1, 2].map((step) => (
            <div key={step} className="flex items-center">
              <div
                className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium transition-all duration-200 ${
                  step <= currentStep
                    ? 'bg-[#6D28D9] text-white'
                    : 'bg-gray-200 text-gray-500'
                }`}
              >
                {step < currentStep ? (
                  <CheckCircle className="h-4 w-4" />
                ) : (
                  step
                )}
              </div>
              {step < 2 && (
                <div
                  className={`w-12 h-0.5 mx-2 transition-all duration-200 ${
                    step < currentStep ? 'bg-[#6D28D9]' : 'bg-gray-200'
                  }`}
                />
              )}
            </div>
          ))}
        </div>
      </div>

        <AnimatePresence mode="wait">
          <motion.div
            key={currentStep}
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -20 }}
            transition={{ duration: 0.3 }}
          >
            {/* Header */}
            <div className="text-center mb-8">
              <h1 className="text-3xl font-medium text-gray-800 mb-3">
                {getStepTitle()}
              </h1>
              <p className="text-gray-600 text-base">
                {getStepDescription()}
              </p>
            </div>
              
                               {/* Step 1: Email */}
                 {currentStep === 1 && (
                   <form onSubmit={handleEmailSubmit} className="space-y-6">
                     <div className="space-y-2">
                       <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                         Email Address
                       </label>
                       <div className="relative">
                         <Input
                           id="email"
                           name="email"
                           type="email"
                           placeholder="your@email.com"
                           value={formData.email}
                           onChange={handleInputChange}
                           className={`pl-10 ${errors.email ? 'border-red-300 focus:border-red-500 focus:ring-red-500/20' : ''}`}
                         />
                         <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                           <Mail className="h-5 w-5 text-gray-400" />
                         </div>
                       </div>
                       {errors.email && (
                         <p className="text-red-400 text-sm">{errors.email}</p>
                       )}
                       {successMessage && (
                         <p className="text-green-500 text-sm">{successMessage}</p>
                       )}
                     </div>

                     <Button 
                       type="submit" 
                       className="w-full bg-[#6D28D9] hover:bg-[#5B21B6] text-white font-medium py-3 rounded-xl transition-all duration-200 hover:shadow-md" 
                       disabled={isLoading}
                     >
                       {isLoading ? (
                         <div className="flex items-center space-x-2">
                           <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                           <span>Sending Code...</span>
                         </div>
                       ) : (
                         <>
                           Send OTP
                           <ArrowRight className="ml-2 h-4 w-4" />
                         </>
                       )}
                     </Button>
                   </form>
                 )}

                 {/* Step 2: OTP Verification & Registration */}
                 {currentStep === 2 && (
                   <form onSubmit={handleOtpSubmit} className="space-y-6">
                     <div className="space-y-4">
                       <label className="block text-sm font-medium text-gray-700 text-center">
                         Enter Verification Code
                       </label>
                       <div className="flex justify-center space-x-3">
                         {formData.otp.map((digit, index) => (
                           <input
                             key={index}
                             ref={(el) => (otpInputs.current[index] = el)}
                             type="text"
                             maxLength="1"
                             value={digit}
                             onChange={(e) => handleOtpChange(index, e.target.value)}
                             onKeyDown={(e) => handleOtpKeyDown(e, index)}
                             className={`w-12 h-12 text-center text-lg font-semibold input-field rounded-xl ${
                               errors.otp ? 'border-red-300 focus:border-red-500 focus:ring-red-500/20' : ''
                             }`}
                           />
                         ))}
                       </div>
                       {errors.otp && (
                         <p className="text-red-400 text-sm text-center">{errors.otp}</p>
                       )}
                     </div>

                     <div className="text-center">
                       <p className="text-sm text-gray-600 mb-3">
                         Didn't receive the code?
                       </p>
                       <Button
                         type="button"
                         variant="ghost"
                         onClick={handleResendOtp}
                         disabled={resendCooldown > 0 || isLoading}
                         className="text-[#6D28D9] hover:text-[#5B21B6]"
                       >
                         {resendCooldown > 0 
                           ? `Resend in ${resendCooldown}s` 
                           : 'Resend Code'
                         }
                       </Button>
                     </div>

                     <Button 
                       type="submit" 
                       className="w-full bg-[#6D28D9] hover:bg-[#5B21B6] text-white font-medium py-3 rounded-xl transition-all duration-200 hover:shadow-md" 
                       disabled={isLoading}
                     >
                       {isLoading ? (
                         <div className="flex items-center space-x-2">
                           <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                           <span>Verifying...</span>
                         </div>
                       ) : (
                         <>
                           Verify Code
                           <ArrowRight className="ml-2 h-4 w-4" />
                         </>
                       )}
                     </Button>
                   </form>
                 )}

                 {/* Login Link */}
                 <div className="text-center mt-6">
                   <p className="text-gray-600">
                     Already have an account?{' '}
                     <Link to="/login" className="text-[#6D28D9] hover:text-[#5B21B6] font-medium transition-colors">
                       Sign in here
                     </Link>
                   </p>
                 </div>
               </motion.div>
             </AnimatePresence>
           </AuthCard>
         )
       }

export default RegisterPage
